mapper.update:
UPDATE available.tb_order
SET
    upd_date=current_timestamp()

        , title = #{}


        , b_time = #{b_time}


        , e_time = #{e_time}


        , content = #{content}


        , output = #{output}


        , note = #{note}


        , status = #{status}


        , import = #{import}


        , delete_yn = #{delete_yn}

WHERE order_idx = #{order_idx};
