mapper.select:
SELECT A.order_idx, A.title, date_format(A.b_time, '%Y-%m-%d %H:%i') AS 'b_time', date_format(A.e_time, '%Y-%m-%d %H:%i') AS 'e_time', A.send_yn, A.output, A.status, A.import, date_format(A.reg_date, '%Y-%m-%d') AS 'reg_date', B.order_price, B.show_price FROM tb_order A inner join tb_price B on A.order_idx = B.order_idx WHERE A.delete_yn = 'N'
AND A.order_idx = #{order_idx}
ORDER BY A.reg_date DESC

mapper.update:
UPDATE available.tb_order
SET
    upd_date=current_timestamp()

        , title = #{}


        , b_time = #{b_time}


        , e_time = #{e_time}


        , content = #{content}


        , output = #{output}


        , note = #{note}


        , status = #{status}


        , import = #{import}


        , delete_yn = #{delete_yn}

WHERE order_idx = #{order_idx};
